public class IfAndOr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int num=90;
		if (num>1 && num <10) {
			System.out.println("this number is small");
		} else if (num>10 && num<100) {
			System.out.println("this number is big" );
		}
			

	}

}
